import React from 'react';
import './App.css';
import {useState} from 'react';
import Clock from './Components/Clock';
import Todo from './Components/Todo';
import Alerts from './Components/Alerts';

function App() {

   const [isLight, setLight] = useState(true)

   const hanClick = () => {
        setLight(isLight  ?  false : true)
      }

  return (

<div className={isLight  ? 'App' : 'LightMode' }>

<header>

<Clock/>
<button className="buttonTog" onClick={hanClick}>{isLight ? <i class="fa fa-sun-o fa-2x "></i> : <i class="fa fa-moon-o fa-2x fa-fw" ></i>}</button>

</header>


<Alerts/>

</div>

);
}

export default App;
