import React from 'react';
import './App.css';
import { useState } from 'react';

function App() {

  const [randHob, setHob] = useState('Irony');

  const hobClick = () => {
  const hobValue = ["German", "Video games", "Lying down", "Reading"]
  const randHob = hobValue[Math.floor(Math.random() * hobValue.length)]
  setHob(randHob);
  }

  const [time, setTime] = useState('');
  const tick = () => {
  const time = new Date().toLocaleTimeString()
  setTime(time);
  }
  setInterval(tick, 1000);

  return (

<div className="App">
<header>

    <h1>Random Hobby Generator</h1>

    <button onClick={hobClick}>
    Click me
    </button>
    <h2>{randHob}</h2>
    <h2  style={{color: "red"}}>The time is {time}</h2>

</header>
</div>

);
}

export default App;
