import React from 'react'
import { useState } from 'react'
import {useRef} from 'react'

export default function Todo() {

    const [randHob, setHob] = useState('Irony');
  const hobClick = () => {
  const hobValue = ["German", "Video games", "Lying down", "Reading"]
  const randHob = hobValue[Math.floor(Math.random() * hobValue.length)]
  setHob(randHob);
  }

    const inputRef = useRef();
    const [alertReminders, setAlertReminders] = useState([]);

    const addAlert = () => {
        const input = inputRef.current;
        const alertPrice = input.value;
        input.value = '';
        setAlertReminders(alertReminders);
      }

    return (
        <div>
        <input ref={inputRef} />
      <button onClick={addAlert}>Add alert</button>
      <div style={{ display: "flex", justifyContent: "flex-end" }}></div>
            <ul>
          {alertReminders.map((alert, i) => (
            <li key={i}> (price number {i + 1} ){alert} </li>
          ))}
        </ul>

        </div>
    )
}
