import './App.css';
import React, {useReducer, useEffect, useState} from 'react'
import moment from "moment"

function reducer(state, action) {
  switch (action.type) {
    case "ADD_ORDER":
      return [...state, action.payload];
    default:
      return state;
  }
}

function App() {




// code for position slider :

let risk = 1 ;
let size = 1;


const [position, setPosition] = useState(risk * size);

  return (


<div className="order-alert-box">

{/* Posotion Sizer */}

<div className="position-sizer-box">
<h4 className="titling">Position Sizer</h4>

<div><p>account balance:</p> 
<input/>
</div>

<div className="slidecontainer">
<p>Risk to reward 1:<strong id="demo"></strong></p>
<input type="range" min="1" max="10" value={position} className="slider" id="myRange"/>
</div>

<div className="slidecontainer">
<p>confidence / volume<strong id="demothree"></strong>%</p>
<input type="range" min="1" max="100" value="3" className="slidertwo" id="myRangethree"/>
</div>

{/* <button className="add-btn" onClick={addPosition}>Add</button> */}

<div><p>Position size:</p><span onChange={() => setPosition(risk * size)}></span></div>

</div>

{/* POSITION SIZER END*/}



</div>

);
}

export default App;
