import React from 'react';
import './App.css';
import { useState } from 'react';

// function handleClick(e) {
//   e.preventDefault();
//   console.log('The link was clicked.');
// }

function App() {

  const [count, setCount] = useState(0);

  const hobValue = ["German", "Video games", "lying down", "reading"]
  const [randhob = hobValue[Math.floor(Math.random() * hobValue.length)], setHob] = useState(randhob);

  return (

<div className="App">
<header>

<h1> Functions</h1>
<div>
      <p>You clicked {count} times</p>
      <button onClick={() => setCount(count + 1)}>
        Click me
      </button>
    </div>

    <p>{randhob}</p>
    <button onClick={() => setHob(randhob)}>
        Click me
      </button>

</header>

</div>
);
}

export default App;
