  const [balance , setBalance] = useState('1000')

  let risk = ( 100 / (riskReward * confirmations)).toFixed(0)

  let positionSize =  (balance / 100 * (riskReward * confirmations)).toFixed(0)

  return (

    <div className="sizer-box">

    <h4 className="titling">Position Sizer</h4>

    <div>
