import React from 'react';
import { useState, useRef} from 'react';

export default function Example() {

    const inputRef = useRef();
    const heading = useRef();
    const selRef = useRef();

    const [list, setList] = useState([]);

    const handleAdd = () => {

        // assign var to use
        let input = inputRef.current;
        let selecting = selRef.current;

        // change style of heading
        heading.current.className = 'off'





          if (input.value !== ''){
            setList([...list, input.value, selecting.value]);
            input.value = ''
        }; 
        console.log(input.value);
          inputRef.current.focus()
        };

    return (
    <div ref={heading} className={"warning"}>
    <h1> Amendment Input </h1>
    <h3> Just numbers, 6 digits, cannot submit empty and remove values</h3>

    <select ref={selRef}>
    <option value="Btc">BTC</option>
    <option value="Eth">ETH</option>
    <option value="Ltc">LTC</option>
    </select>

    <input ref={inputRef}  maxLength="6" pattern="/[0-9]/"/>

    <button onClick={handleAdd}> Add item</button>

    <ul>
          {list.reverse().map((item, i) => (
            <li key={i}>{item} 
            </li>
          ))}
        </ul>
    </div>
    )
}
