import React from 'react';
import './App.css';
import {useState} from 'react';
import Clock from './Components/Clock';


function App() {

   const [isToggleOn, setToggle] = useState(true)
   const [isLight, setLight] = useState(true)

   const hanClick = () => {
        setToggle(isToggleOn  ? false : true)
        setLight(isLight  ?  false : true)
      }

  return (

<div className={isLight  ? 'App' : 'darkMode' }>

<header>
<button onClick={hanClick}>{isToggleOn ? 'LIGHT' : 'DARK'}</button>
<Clock/>
  <i class="fa fa-moon-o fa-3x fa-fw" ></i>
  <i class="fa fa-sun-o fa-3x "></i>
</header>

</div>

);
}

export default App;
