import './App.css';
import React, {useState} from 'react';

function App() {

  const [inputz, setInput] = useState(1); // '' is the initial state value

  let risk = 1;



  return (

<div className="order-alert-box">

{/* Posotion Sizer */}
<div className="position-sizer-box">


<h4 className="titling">Position Sizer</h4>

<div>
<label>Please specify:</label>
</div>

<div><p>account balance:</p><input/></div>

<div className="slidecontainer">
  <p>Risk to reward 1:<strong id="demo"></strong>{inputz}</p>
<input onInput={e => setInput(e.target.value)} defaultValue="2" type="range" min="1" max="10" className="slider"/>
</div>

<div><p>Position size:</p><span>{ risk }</span></div>

</div>

{/* POSITION SIZER END*/}

</div>

);
}

export default App;



