import React, { Component } from 'react'
import { useState } from 'react'

export default function Clock() {

    const [time, setTime] = useState('');

    const tick = () => {
    const time = new Date().toLocaleTimeString()
    setTime(time);

    }

    setInterval(tick, 1000);

    return (
        <div className="my-clock">
        {time}
        </div>
    )
}
