import React from 'react';
import { useState, useRef} from 'react';

export default function Example() {

    const inputRef = useRef();
    const selectRef = useRef();
    const [list, setList] = useState([]);

    const handleAdd = () => {

        const input = inputRef.current;
        const selecting = selectRef.current;

        const order = { input, selecting}

        console.log(input.value)
        console.log(selecting.value)
        console.log(input.pattern)
        console.log(selecting)

        if (input.value !== ''){
            setList([...list, input.value, selecting.value]);
        }  
          input.value = ''
        };



    return (
    <div>
    <h1> Amendment Input </h1>
    <h3> Just numbers, 6 digits, cannot submit empty and remove values</h3>

    <select ref={selectRef}>
    <option value="Btc">BTC</option>
    <option value="Eth">ETH</option>
    <option value="Ltc">LTC</option>
    </select>

    <input ref={inputRef}  maxLength="6" pattern="[0-9]"/>

    <button type="submit" onClick={handleAdd}> Add item</button>

    <ul>
          {list.reverse().map((item, i) => (
            <li key={i}>{item} 
            </li>
          ))}
        </ul>


    </div>
    )
}
