import React from 'react';
import './App.css';
// import ChangeArea from './Components/ChangeArea';
// import Toggle from './Components/Toggle';
import Example from './Components/Example';
import {useState} from 'react';

function App() {

  const heading = React.useRef();

  const [numbers, setState] = useState()

   const number = [1, 2, 3, 4, 5]

    const lightMode = () => {
    heading.current.className = 'darkMode' 
    setState(number.map((number) => number * 2));
        };

    const darkMode = () => {
        heading.current.className = 'App' 
        let numbers = [1, 2, 3, 4, 5]
        const tripled = numbers.map((number) => number * 3);
        setState(tripled);
            };






  return (


<div ref={heading} className="App">
<button onClick={lightMode}>light mode</button>
<button onClick={darkMode}>dark mode</button>

<p>{numbers}</p>

<header>
<p> hello there</p>
</header>

</div>

);
}

export default App;
