import './App.css';
import { useState } from 'react';
import Time from './Components/Time';

function App() {

  const [randHob, setHob] = useState('Irony');

  const hobClick = () => {
  const hobValue = ["German", "Video games", "Lying down", "Reading"]
  const randHob = hobValue[Math.floor(Math.random() * hobValue.length)]
  setHob(randHob);
  }



  return (

<div className="App">
<header>

    <h1>Random Hobby Generator</h1>

    <button onClick={hobClick}>
    Click me
    </button>
    <h2>{randHob}</h2>
    <Time></Time>

</header>
</div>

);
}

export default App;
