import React from 'react';
import moment from "moment";
import './bell.css';
import './orderbook.css';
import './pricealert.css';
import { useState, useRef} from 'react';

function reducer(state, action) {
  switch (action.type) {
    case "ADD_ORDER":
      return [...state, action.payload];
    default:
      return state;
  }
}

function Alerts() {
  const inputRef = React.useRef();
  const [alertReminders, setAlertReminders] = React.useState([]);
  const [orders, dispatch] = React.useReducer(reducer, []);

  const addAlert = () => {
    const input = inputRef.current;
    const alertPrice = input.value;


      setAlertReminders([...alertReminders, alertPrice]);


    input.value = ''
  };

  const removeAlert = (i) => {
    const newAlerts = alertReminders.filter((_, alertIndex) => alertIndex !== i)
    setAlertReminders(newAlerts);

  };

  React.useEffect(() => {
    var ws = new WebSocket("wss://api.bitfinex.com/ws");

    ws.onopen = function () {
      ws.send(
        JSON.stringify({
          event: "subscribe",
          channel: "trades",
          pair: "tBTCUSD"
        })
      );
    };

    ws.onmessage = function (msg) {
      var response = JSON.parse(msg.data);
      var isAnOrder = response[1] === "tu";

      if (isAnOrder) {
        const time = moment().format("hh:mm:ss");
        const price = response[5].toFixed(0);
        const amount = response[6].toFixed(5);

        const order = { time, price, amount };

        dispatch({ type: "ADD_ORDER", payload: order })

        return () => ws.close(1000, 'Deliberate disconnection')
      }
    };
  }, []);


  let alerting = '';
  const stopBell = () => {
    heady.current.className = 'fa fa-bell'
    alerting = '';
  }

  const heady = useRef();

  // 2-d array
  orders.forEach(p => {
    alertReminders.forEach(j => {
     let check = parseInt(j) - parseInt(p.price) 
     if (Math.abs(check) <  100) {

      heady.current.className = 'fa fa-bell shake';
      alerting =  ' the price is ' + p.price ; 
      // window.alert("the price is"); 

      setTimeout(stopBell, 3000)

       }
    }) 
  })


  if(orders.length > 5){
    orders.length = orders.length - 1;
    orders.splice(9);
  };

  orders.reverse()

  console.log(orders);

  return (
    <div className="orders-Alerts">
      <section> 

      <h1>{alerting}</h1>
      <i ref={heady} className={"fa fa-bell"} aria-hidden="true"></i>

      </section>

      <div className="Alertbox">
        <div className="Header">
          <h4 className="Titling">Add Alert</h4>

          <input className="Myinput" ref={inputRef} placeholder=" price for alert" />
          <button className="Addbtn" onClick={addAlert}>Add</button>
        </div>

        <ul className="uling">
          {alertReminders.reverse().map((alert, i) => (
            <li className="Myli" key={i}>
           <i className="fa fa-btc"> </i>
           <strong> { alert }  </strong>
            <i className="fa fa-remove" onClick={() => removeAlert(i)}> </i>

            </li>
          ))}
        </ul>

      </div>

      <div className="Orderbook">
        <h4 className="Titling"> Bitfinex BTC order book</h4>
        <table>
          <thead>
            <tr>
              <th>Time </th>
              <th>Price</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(({ time, price, amount }, i) => (
              <tr key={i}>

                <td>{i} {time} </td><td>{price}</td><td>{amount}</td>

              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Alerts;
