import React from 'react';
import moment from "moment";
import './bell.css';
import './orderbook.css';
import './pricealert.css';
import {useRef} from 'react';

function reducer(state, action) {
  switch (action.type) {
    case "ADD_ORDER":
      return [...state, action.payload];
    default:
      return state;
  }
}

function Alerts() {

  const inputRef = React.useRef();
  const [alertReminders, setAlertReminders] = React.useState([]);
  const [order, dispatch] = React.useReducer(reducer, []);

  const addAlert = () => {
  const input = inputRef.current;
  const alertPrice = input.value;

  const patt = new RegExp(/^\d+$/);
  const check = patt.test(input.value);


  if (input.value === '' || check === false){
            alert('Number please')
        }

    if (input.value !== '' && check === true){
          setAlertReminders([...alertReminders, alertPrice]);
    }

      input.value = ''
  };

  const removeAlert = (i) => {
    const newAlerts = alertReminders.filter((_, alertIndex) => alertIndex !== i)
    setAlertReminders(newAlerts);
  };

  React.useEffect(() => {
    const ws = new WebSocket("wss://api.bitfinex.com/ws");

    ws.onopen = function () {
      ws.send(
        JSON.stringify({
          event: "subscribe",
          channel: "trades",
          pair: "tBTCUSD"
        })
      );
    };

    ws.onmessage = function (msg) {
      var response = JSON.parse(msg.data);
      var isAnOrder = response[1] === "tu";

      if (isAnOrder) {
        const time = moment().format("hh:mm:ss");
        const price = response[5].toFixed(0);
        const amount = response[6].toFixed(5);

        const order = { time, price, amount };

        dispatch({ type: "ADD_ORDER", payload: order })

      }
    };
  }, []);


  let alerting = '';
  const stopBell = () => {
    heady.current.className = 'fa fa-bell'
    alerting = '...';
  }

  const heady = useRef();

  // 2-d array
  order.forEach(p => {
    alertReminders.forEach(j => {

     let check = parseInt(j) - parseInt(p.price) 

     if (Math.abs(check) <  100) {

      heady.current.className = 'fa fa-bell shake';
      alerting =  'The price is ' + p.price ; 
      setTimeout(stopBell, 3000)

       }
    }) 
  })

  const flashRef = React.useRef();

  if(order.length > 8){
    order.pop();
  };

  order.reverse(order.indexOf)

  return (
    <div className="orders-alerts">
      <section className="belly-box"> 


      <i ref={heady} className={"fa fa-bell"}></i>

      <h4 className="titling">{alerting}</h4>

      </section>

      <div className="alert-box">
        <div className="header">
          <h4 className="titling">Add Alert</h4>

          <input className="my-input" ref={inputRef} placeholder="price for alert"  maxLength="10" />
          <button className="add-btn" onClick={addAlert}>Add</button>
        </div>

        <ul className="uling">
          {alertReminders.reverse().map((alert, i) => (
            <li className="my-li" key={i}>
           <i className="fa fa-btc"> </i>
           <strong> { alert }  </strong>
            <i className="fa fa-remove" onClick={() => removeAlert(i)}> </i>

            </li>
          ))}
        </ul>

      </div>

      <div className="orderbook">
        <h4 className="titling"> Bitfinex BTC order book</h4>
        <table>
          <thead>
            <tr>
              <th>Time </th>
              <th>Price</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {order.forEach(({ time, price, amount }, i) => (
              <tr  key={i}  ref={flashRef} className={"flash"} >

                <td> {time} </td><td>{price}</td><td>{amount}</td>

              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Alerts;
