import React from 'react';
import './App.css';
import {useState} from 'react';
import Clock from './Components/Clock';
import Alerts from './Components/Alerts';

function App() {

   const [isLight, setLight] = useState(true)

   const hanClick = () => {
        setLight(isLight  ?  false : true)
      }

  return (

<div className={isLight  ? 'App' : 'lightMode' }>

<header  className="top-head">

<Clock/>
<div className="btn-tog" onClick={hanClick}>{isLight ? <i className="fa fa-sun-o fa-2x " style={{ color: 'white'}}></i> : <i className="fa fa-moon-o fa-2x fa-fw" ></i>}</div>
</header>

<div className="middle-boxes">
<Alerts/>
</div>


</div>

);
}

export default App;
