    const [minute, setMinute] = useState(0)

    let calls = -1

    const handleAdd = () => {
        if (minute < 3) {
            setMinute(minute + 1)


        } else {
            setMinute('Less than 3 please')
        }

    }

    return (
    <div style={{backgroundColor : 'grey', padding: '10px'}}>
    <div> 

    <button onClick={handleAdd}></button>
    <h4> {minute} </h4>
    </div>
    </div>
    )
}
