  const hobValue = ["German", "Video games", "lying down", "reading"]
  const randhob = hobValue[Math.floor(Math.random() * hobValue.length)]
  const [setHob] = useState(randhob);

  setHob = () => {

    const hobValue = ["German", "Video games", "lying down", "reading"]
    const randhob = hobValue[Math.floor(Math.random() * hobValue.length)]
  }


  return (

<div className="App">
<header>

<h1> Functions</h1>
<div>
      <p>You clicked {count} times</p>
      <button onClick={() => setCount(count + 1)}>
        Click me
      </button>
    </div>

    <p>{randhob}</p>
    <button onClick={setHob()}>
        Click me
      </button>

</header>

</div>
);
}

export default App;
