import React, {useState} from 'react';

function App() {

  const [riskReward, setInput] = useState(2)
  const [inputTwo, setInputTwo] = useState(2)

  let risk = 1;

  let balance = 3500;



  return (

<div className="order-alert-box">

{/* Posotion Sizer */}
<div className="position-sizer-box">

<h4 className="titling">Position Sizer</h4>

<div><label>Account Balance:</label><input/></div>

<div className="slidecontainer">
<label>Risk to Reward 1:<strong>{riskReward}</strong></label>
<input onInput={e => setInput(e.target.value)} defaultValue="2" type="range" min="1" max="10" className="slider"/>
</div>

<div className="slidecontainer">
<label>Confirmations:  <strong>{inputTwo}</strong></label>
<input onInput={e => setInputTwo(e.target.value)} defaultValue="7" type="range" min="1" max="10" className="slider"/>
</div>

<div> Position Size : <span>Â£  { balance / 100 * (riskReward * inputTwo)}</span></div>

<div>Risk<span> % { balance / 100 }</span></div>

</div>

{/* POSITION SIZER END*/}

</div>

);
}

export default App;
