import React from 'react';
import { useState, useEffect, useRef} from 'react';

export default function Example() {

    const inputRef = useRef();
    const [alertReminders, setAlertReminders] = React.useState([]);

    const handleAdd = () => {

        const input = inputRef.current;
        const alertPrice = input.value;

        if (alertReminders.indexOf(alertPrice) === -1) {
            setAlertReminders([alertReminders, alertPrice]);
          }
          input.value = ''
        };

    return (
    <div>
    <input ref={inputRef} />
    <button onClick={handleAdd}></button>
    <ul className="Inneralert">
          {alertReminders.reverse().map((n, i) => (
            <li className="Myli" key={i}>{n}
            </li>
          ))}
        </ul>
    </div>
    )
}
