import React from 'react';
import './App.css';
import { useState } from 'react';

function App() {

  const [randHob, setHob] = useState('Irony');

  const hobClick = () => {
  const hobValue = ["German", "Video games", "lying down", "reading"]
  const randHob = hobValue[Math.floor(Math.random() * hobValue.length)]
  setHob([randHob]);
  }


  function tick() {
  new Date().toLocaleTimeString()
  setInterval(tick, 1000);
  }


  return (

<div className="App">
<header>

    <h1>Random Hobby Generator</h1>

    <button onClick={hobClick}>
    Click me
    </button>
    <h2>{randHob}</h2>
    <h2>It is {new Date().toLocaleTimeString()}.</h2>

</header>
</div>

);
}

export default App;
