import React from 'react';
import { useState, useRef} from 'react';

export default function Example() {

    const inputRef = useRef();
    const select = useRef();


    const [list, setList] = useState([]);

    const handleAdd = () => {

        const type = select.current;
        const inp = inputRef.current;


        // const patt1 = /[1-9]/;
        // if (newItem === newItem.match(patt1)){
        //     console.log(newItem)
        // }
        console.log(input.value)
        if (input.value !== ''){
            setList([...list, input.value, type]);
        }  
          input.value = ''
        };

        // console.log(inputRef.current)

    return (
    <div>
    <h1> Amendment Input </h1>
    <h3> Just numbers, 6 digits, cannot submit empty and remove values</h3>


    <select ref={select} name="petType">
  <option value="cat">Cat</option>
  <option value="dog">Dog</option>
  <option value="ferret">Ferret</option>
</select>

    <input ref={inputRef}  maxLength="6" pattern="[0-9]"/>

    <button type="submit" onClick={handleAdd}> Add item</button>

    <ul>
          {list.reverse().map((item, i) => (
            <li key={i}>{item}
            </li>
          ))}
        </ul>
    </div>
    )
}
