import './App.css';
import React, {useState} from 'react';

function App() {

  const [riskReward, setRr] = useState(2)
  const [confirmations, setconfirmations] = useState(2)
  const [balance , setBalance] = useState('1000')

  let risk = ( 100 / (riskReward * confirmations)).toFixed(0)

  let positionSize =  (balance / 100 * (riskReward * confirmations)).toFixed(0)

  return (

    <div className="sizer-box">

    <h1>Position Sizer</h1>

    <div className="container"> 
    Account Balance:
    <input className="balance" onInput={e => setBalance(e.target.value)}  placeholder={'1000'}/>
    </div>

    <div className="container">
    Risk to Reward 1: {riskReward}
    <input className="slider" onInput={e => setRr(e.target.value)} defaultValue="2" type="range" min="1" max="10" />
    </div>

    <div className="container"> Confirmations:  {confirmations}
    <input onInput={e => setconfirmations(e.target.value)} defaultValue="2" type="range" min="1" max="10" className="slider"/>
    </div>

    <div className="container"> 
     Your Position Size : Â£  {positionSize}
     <br></br>
     Your Risk  % { risk }
     </div>


    </div>

);
}

export default App;
