import React from 'react';
import './App.css';
// import Example from './Components/Example';
import {useState} from 'react';

function App() {

  const heading = React.useRef();

  const number = [1, 2, 3, 4, 5]

  const [numbers, setNums] = useState(number)

    const lightMode = () => {
    heading.current.className = 'darkMode' 
    setNums(number.map((n) => n * 2));
        };

    const darkMode = () => {
    heading.current.className = 'App' 
    setNums(number.map((n) => n * 3));
        };

  return (


<div ref={heading} className="App">
<button onClick={lightMode}>light mode</button>
<button onClick={darkMode}>dark mode</button>

<p>{numbers}</p>

<header>
<p> hello there</p>
</header>

</div>

);
}

export default App;
