import React, { Component } from 'react'
import { useState } from 'react'

export default function Clock() {

    const [time, setTime] = useState('');

    const tick = () => {
    const time = new Date().toLocaleTimeString()
    setTime(time);
    }

    setInterval(tick, 1000);

    return (
        <div>
             <h2>{time}</h2>
        </div>
    )
}
