import React from 'react';
import './App.css';
import { useState } from 'react';

// function handleClick(e) {
//   e.preventDefault();
//   console.log('The link was clicked.');
// }

function App() {
  const [input, setInput] = useState('');
  const [count, setCount] = useState(0);

  const hobValue = ["German", "Video games", "lying down", "reading"]
  const randhob = hobValue[Math.floor(Math.random() * hobValue.length)]
  const [setHob] = useState(randhob);

  return (

<div className="App">
<header>

<div>
    <label>Please specify:</label>
    <input value={input} onInput={e => setInput(e.target.value)}/>
    </div>

<h1> Functions</h1>
<div>
      <p>You clicked {count} times</p>
      <button onClick={() => setCount(count + 1)}>
        Click me
      </button>
    </div>

    <p>{randhob}</p>
    <button onClick={setHob}>
        Click me
      </button>

</header>

</div>
);
}

export default App;
