import React, { useEffect } from "react";
import ReactDOM from "react-dom";
import moment from "moment";


function reducer(state, action) {
  switch (action.type) {
    case "ADD_ALERT":
      return [...state, action.payload];
    default:
      return state;
  }
}

export default function Trade() {
  const inputRef = React.useRef();
  const [alertReminders, setAlertReminders] = React.useState([]);
  const [alerts, dispatch] = React.useReducer(reducer, []);

  const addAlert = () => {
    const input = inputRef.current;
    const alertPrice = input.value;

    setAlertReminders([...alertReminders, alertPrice]);

  };

  useEffect(() => {
    var ws = new WebSocket("wss://api.bitfinex.com/ws");

    ws.onopen = function() {
      ws.send(
        JSON.stringify({
          event: "subscribe",
          channel: "trades",
          pair: "tBTCUSD"
        })
      );
    };


    ws.onmessage = function(msg) {
        var response = JSON.parse(msg.data);
        var isAnOrder = response[1] === "tu";

        if (isAnOrder) {
          var time = moment().format("HH:mm:ss");

          var price = response[5].toFixed(1);
          var amount = response[6].toFixed(5);

          var alert = { time, price, amount };

          alertReminders.forEach(p => {
            if (Math.abs(p - price) % 500) {
              window.alert("HEY!");
            }
          });

          dispatch({ type: "ADD_ALERT", payload: alert });
        }
      };
    }, []);


