//         var alert = { time, price, amount };
//         dispatch({ type: "ADD_ALERT", payload: alert });

//     };
//   }, []);

//   const check = (price, alertPrice) => {
//     if (price === alertPrice) {
//       window.alert("HEY!");
//     }
//   }
//   alertReminders.forEach(check);

//   console.log(typeof price);
//   console.log(alertReminders[0]);

  return (
    <div className="App">
      <input ref={inputRef} />

      <button onClick={addAlert}>Add alert</button>
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <ul>
          {alertReminders.map((alert, i) => (
            <li key={i}>{alert}</li>
          ))}
        </ul>
        <ul>
          {alerts.map(({ time, price, amount }, i) => (
            <li key={i}>

             <p>
                Date {time}, Value {price}, Amount {amount}
              </p>

            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
