import React from 'react';
import moment from "moment";
import './Apping.css';
import './orderbook.css';
import './pricealert.css';
import { useState, useRef} from 'react';

function reducer(state, action) {
  switch (action.type) {
    case "ADD_ORDER":
      return [...state, action.payload];
    default:
      return state;
  }
}

function Alerts() {
  const inputRef = React.useRef();
  const [alertReminders, setAlertReminders] = React.useState([]);
  const [orders, dispatch] = React.useReducer(reducer, []);

  const addAlert = () => {
    const input = inputRef.current;
    const alertPrice = input.value;


      setAlertReminders([...alertReminders, alertPrice]);


    input.value = ''
  };


  const removeAlert = (i) => {
    const newAlerts = alertReminders.filter((_, alertIndex) => alertIndex !== i)
    setAlertReminders(newAlerts);

  };

  React.useEffect(() => {
    var ws = new WebSocket("wss://api.bitfinex.com/ws");

    ws.onopen = function () {
      ws.send(
        JSON.stringify({
          event: "subscribe",
          channel: "trades",
          pair: "tBTCUSD"
        })
      );
    };

    ws.onmessage = function (msg) {
      var response = JSON.parse(msg.data);
      var isAnOrder = response[1] === "tu";

      if (isAnOrder) {
        const time = moment().format("HH:mm:ss");
        const price = response[5].toFixed(1);
        const amount = response[6].toFixed(5);

        const order = { time, price, amount };

        dispatch({ type: "ADD_ORDER", payload: order })

        return () => ws.close(1000, 'Deliberate disconnection')
      }
    };
  }, []);

  alertReminders.forEach(p => {
            if (p - orders <  100) 
                 window.alert("the price is");
             })

  console.log(orders)
  orders.forEach(element => { console.log('the price', element.price, element) })


  const heading = useRef();


  const [shake, setShake] = useState("fa fa-bell shake")

  // "fa fa-bell shake"
  // "fa fa-bell"

  // heading.current.className = 'fa fa-bell'
  // console.log(heading.current.className)
  // console.log(heading)

  orders.forEach(p => {
  alertReminders.forEach(j => {
  if (p.price - j <  100){
  console.log("the price is",  p.price);
  let shake = "fa fa-bell shake";
  }
  }) 
  })

  if(orders.length > 9){
    orders.length = orders.length - 1;
  };

  return (
    <div className="orders-Alerts">
      <i ref={heading} className={shake} aria-hidden="true"></i>
      <div className="Alertbox">
        <div className="Header">
          <h4>Add Alert</h4>
          <input className="Myinput" ref={inputRef} placeholder=" price for alert" />
          <button className="Addbtn" onClick={addAlert}>Add</button>
        </div>

        <ul className="Inneralert">
          {alertReminders.reverse().map((alert, i) => (
            <li className="Myli" key={i}>{alert}
              <button className="Removebtn" onClick={() => removeAlert(i)}>x</button>
            </li>
          ))}
        </ul>

      </div>

      <div className="Orderbook">
        <h4> Bitfinex BTC order book</h4>
        <table>
          <thead>
            <tr>
              <th>Time </th>
              <th>Price</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {orders.reverse().map(({ time, price, amount }, i) => (
              <tr key={i}>

                <td> {time} </td><td>{price}</td><td>{amount}</td>

              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Alerts;
