import React from 'react'
import { useState, useRef } from 'react'

export default function Todo() {


    // could try to add new item from random API

    // ref to value
    const todoRef = useRef();
    // 
    const [todoReminders, setTodo] = useState([]);
    // 
    const addTodo = () => {
        const input = todoRef.current;
        const todo = input.value;

    // set todo in todoReminders array
        setTodo([...todoReminders, todo]);

        input.value = '';
      }

    // map function 
    return (
        <div>
        <input ref={todoRef} />
      <button onClick={addTodo}>Add alert</button>
      <div></div>
            <ul>

          {todoReminders.map((todo, i) => (
            <li key={i}> (number {i + 1} ) <h6> {todo} </h6></li>
          ))}
        </ul>

        </div>
    )
}
