import React from 'react';
import './App.css';
import {useState} from 'react';

function App() {

  const heading = React.useRef();

    const lightMode = () => { heading.current.className = 'darkMode'  };
    const darkMode = () => { heading.current.className = 'App' };


   const [isToggleOn, setToggle] = useState(true)
   const [isLight, setLight] = useState(true)

   const hanClick = () => {
        setToggle(isToggleOn  ? false : true)

        setLight(isLight  ? false : true)
      }

  return (

<div ref={heading} className="App">
<header>
<button onClick={lightMode}>light mode</button>
<button onClick={darkMode}>dark mode</button>
</header>
<button onClick={hanClick}>{isToggleOn ? 'LIGHT' : 'DARK'}
</button>

<h1> Hello There</h1>
<h3></h3>

</div>

);
}

export default App;
