import './App.css';
import React, {useReducer, useEffect} from 'react'
import moment from "moment"




function App() {

  const [order, dispatch] = useReducer(reducer, [])

React.useLayoutEffect(() => {
  const ws = new WebSocket("wss://api.bitfinex.com/ws")

  ws.onopen = function () {
    ws.send(
      JSON.stringify({
        event: "subscribe",
        channel: "trades",
        pair: "tBTCUSD"
      })
    )
  }

  ws.onmessage = function (msg) {
    var response = JSON.parse(msg.data)
    var isAnOrder = response[1] === "tu"

    if (isAnOrder) {
      const time = moment().format("hh:mm:ss")
      const price = response[5].toFixed(0)
      const amount = response[6].toFixed(5)
      const order = { time, price, amount }
      dispatch({ type: "ADD_ORDER", payload: order })
    }

  }
}, [])

function reducer(state, action) {

  return [...state, action.payload]

}



  return (





<div className="order-alert-box">

<div className="orderbook">


</div>



<div className="orderbook">
  <h4 className="titling"> Bitfinex BTC order book</h4>
  <table>
    <thead>
      <tr>
        <th>Time </th>
        <th>Price</th>
        <th>Amount</th>
      </tr>
    </thead>
    <tbody>
      {order.map(({ time, price, amount }, i) => (
        <tr  key={i}>
          <td> {time} </td><td>{price}</td><td>{amount}</td>

        </tr>
      ))}
    </tbody>
  </table>
</div>
</div>



);
}

export default App;
