import React from 'react';
import { useState, useRef} from 'react';

export default function Example() {

    const inputRef = useRef();
    const [list, setList] = useState([]);

    const handleAdd = () => {

        const input = inputRef.current;
        const newItem = input.value;

        // const patt1 = /[1-9]/g;
        // && newItem === newItem.match(patt1)
        console.log(inputRef.pattern)

        if (newItem !== ''){
            setList([...list, newItem]);
        }  
          input.value = ''
        };

        // console.log(inputRef.current)

    return (
    <div>
    <h1> Amendment Input </h1>
    <h3> Just numbers, 6 digits, cannot submit empty and remove values</h3>
    <form action="">
    <input ref={inputRef}  maxlength="6"  type="numbers"/>
    <button type="submit" onSubmit={handleAdd}> Add item</button>
    </form>
    <ul>
          {list.reverse().map((item, i) => (
            <li key={i}>{item}
            </li>
          ))}
        </ul>
    </div>
    )
}
